public class pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=5;
		for(int i=1;i<=5;i++)
		{
		
			for(int j=1;j<=count;j++){
				System.out.print("* ");
			}
			System.out.println();
			count--;
		}
		
	}

}
